import { Component } from "@angular/core";
import { Tableau } from "../tableau";

@Component({
  selector: "app-qlqstableaux",
  templateUrl: "./qlqstableaux.component.html",
  styleUrls: ["./qlqstableaux.component.css"],
})
export class QlqstableauxComponent {
  currentDate: Date = new Date();
  tableaux: Tableau[] = [
    new Tableau(
      "La persistance de la mémoire",
      "assets/Tabs/t3.jpg",
      "Salvador Dali",
      1850,
      "surrealisme"
    ),
    new Tableau(
      "Guernica",
      "assets/Tabs/t1.jpg",
      "Pablo Picasso",
      3200,
      "surrealisme"
    ),
    new Tableau(
      "La corbeille de pommes",
      "assets/Tabs/t2.jpg",
      "Paul Cézanne",
      2400,
      "post impressionisme"
    ),
  ];
}
<h1>Ventes aux enchères du: {{ currentDate | date:'* dd * MMMM * yyyy *' }}</h1>
<div>
    <table class="table table-striped"></table>
</div>
